func afterTest() {
	os.RemoveAll("./retry.bin")
}

func TestNewRetryDB(t *testing.T) {
	defer afterTest()
	_, err := NewRetryDB("./", 5, 1)
	if err != nil {
		t.Fatal(err)
	}
}

func TestRetryDB_Put(t *testing.T) {
	defer afterTest()
	db, _ := NewRetryDB("./", 5, 1)
	err := db.Put(txArr[0])
	if err != nil {
		t.Fatal(err)
	}

	val, err := db.GetAll()
	if err != nil {
		t.Fatal(err)
	}
	if len(val) != 1 || val[0] != txArr[0] {
		t.Fatal("not right val")
	}

}

func TestRetryDB_GetAll(t *testing.T) {
	defer afterTest()
	db, _ := NewRetryDB("./", 2, 1)
	for _, tx := range txArr[:3] {
		db.Put(tx)
	}

	vals, err := db.GetAll()
	if err != nil {
		t.Fatal(err)
	}
	if len(vals) != 3 {
		t.Fatal("not right length 3")
	}

	for _, tx := range txArr[3:] {
		db.Put(tx)
	}
	vals, err = db.GetAll()
	if err != nil {
		t.Fatal(err)
	}
	if len(vals) != 7 {
		t.Fatal("not right length 7 ")
	}
	vals, err = db.GetAll()
	if err != nil {
		t.Fatal(err)
	}
	if len(vals) != 4 {
		t.Fatal("not right length 4")
	}

	_, err = db.GetAll()
	if err == nil {
		t.Fatal("err should not be nil")
	}
}

func TestRetryDB_GetAll2(t *testing.T) {
	defer afterTest()
	db, _ := NewRetryDB("./", 0, 1)
	for _, tx := range txArr[:3] {
		db.Put(tx)
	}
	vals, err := db.GetAll()
	if err != nil {
		t.Fatal(err)
	}
	if len(vals) != 3 {
		t.Fatal("not right length 3")
	}
	vals, err = db.GetAll()
	if err != nil {
		t.Fatal(err)
	}
	if len(vals) != 3 {
		t.Fatal("length shouldn't change")
	}
}

